mapper.update:
update article
set comment_counts = #{article.commentCounts}
,create_date = #{article.createDate}
,summary = #{article.summary}
,title = #{article.title}
,view_counts = #{article.viewCounts}
,weight = #{article.weight}
,category_id = #{article.categoryId}
where id = #{article.id}

