mapper.insert:


    select LAST_INSERT_ID()

insert into sys_user(account
    ,admin
    ,avatar
    ,create_date
    ,deleted
    ,email
    ,last_login
    ,mobile_phone_number
    ,nickname
    ,password
    ,salt
    ,status)
values (#{sysUser.account}
    ,#{sysUser.admin}
    ,#{sysUser.avatar}
    ,#{sysUser.createDate}
    ,#{sysUser.deleted}
    ,#{sysUser.email}
    ,#{sysUser.lastLogin}
    ,#{sysUser.mobilePhoneNumber}
    ,#{sysUser.nickname}
    ,#{sysUser.password}
    ,#{sysUser.salt}
    ,#{sysUser.status})

mapper.select:
select id, nickname, account
from sys_user
where account = #{account}

select id, nickname, avatar
from sys_user
where id = #{id}
